// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PermitUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20CappedUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

/**
 * @title USDCToken
 * @dev USDC token contract with hardcoded supply and cap
 * @notice This contract provides USDC token functionality with upgradeable features
 *
 * Key Features:
 * - ERC20 token with upgradeable functionality
+ * - Hardcoded initial supply: 10,000,000 USDC (10M tokens)
+ * - Hardcoded cap: 10,000,000,000 USDC (10B tokens)
 * - Owner-controlled minting and burning
 * - Pausable functionality
 * - Permit functionality for gasless approvals
 * - 6 decimals (standard for USDC)
 */
contract USDCToken is
    Initializable,
    ERC20PermitUpgradeable,
    ERC20PausableUpgradeable,
    ERC20CappedUpgradeable,
    ERC20BurnableUpgradeable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable
{
    // Constants
    uint256 public constant INITIAL_SUPPLY = 10_000_000 * 10 ** 6; // 10M USDC with 6 decimals
    uint256 public constant MAX_SUPPLY = 10_000_000_000 * 10 ** 6; // 10000M USDC with 6 decimals

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the USDCToken contract
     */
    function initializeToken() external virtual initializer {
        _initializeToken();
    }

    function _initializeToken() internal {
        // cuz we are using integrated Token+Contract method
        string memory name_ = "Money Pot";
        string memory symbol_ = "USDC";

        __ERC20_init(name_, symbol_);
        __ERC20Permit_init(name_);
        __ERC20Capped_init(MAX_SUPPLY);
        __Pausable_init();
        __Ownable_init();
        __ReentrancyGuard_init();
        __UUPSUpgradeable_init();

        address _initialOwner = msg.sender;

        // Mint initial supply to the initial owner
        _mint(_initialOwner, INITIAL_SUPPLY);
    }

    /**
     * @dev Authorize contract upgrades (only owner)
     */
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    // ============ ADMIN FUNCTIONS ============

    /**
     * @dev Pause contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Mint tokens (only owner)
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev Burn tokens from account (only owner)
     * @param from Address to burn tokens from
     * @param amount Amount of tokens to burn
     */
    function burn(address from, uint256 amount) external {
        require(
            msg.sender == from || msg.sender == owner(),
            "Only the owner of the token or the token holder can burn tokens"
        );
        _burn(from, amount);
    }

    // ============ INTERNAL FUNCTIONS ============

    /**
     * @dev Override _mint to respect cap
     */
    function _mint(
        address to,
        uint256 amount
    ) internal override(ERC20Upgradeable, ERC20CappedUpgradeable) {
        super._mint(to, amount);
    }

    /**
     * @dev Override _beforeTokenTransfer to handle all token transfers
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20Upgradeable, ERC20PausableUpgradeable) {
        super._beforeTokenTransfer(from, to, amount);
    }

    /**
     * @dev Override decimals to return 6 (standard for USDC)
     */
    function decimals() public pure override returns (uint8) {
        return 6;
    }
}

contracts/UnrealBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./UnrealToken.sol";

/**
 * @title UnrealBridge
 * @dev Bridge contract for $UNREAL utility token with 1inch Fusion+ integration
 * Implements hashlock/timelock mechanism for cross-chain atomic swaps
 */
contract UnrealBridge is Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable {

    UnrealToken public unrealToken;

    // Bridge intent structure for Fusion+ orders
    struct BridgeIntent {
        address user;           // User initiating the bridge
        uint256 amount;         // Amount to bridge
        uint256 sourceChainId;  // Source chain ID
        uint256 targetChainId;  // Target chain ID
        bytes32 hashlock;       // Hash of the secret
        uint256 timelock;       // Expiration timestamp
        bool completed;         // Whether intent is completed
        bool refunded;          // Whether intent is refunded
    }

    // Mapping from intent hash to bridge intent
    mapping(bytes32 => BridgeIntent) public bridgeIntents;

    // Events for Fusion+ integration
    event BridgeIntentCreated(
        bytes32 indexed intentHash,
        address indexed user,
        uint256 amount,
        uint256 sourceChainId,
        uint256 targetChainId,
        bytes32 hashlock,
        uint256 timelock
    );

    event BridgeIntentCompleted(
        bytes32 indexed intentHash,
        bytes32 secret
    );

    event BridgeIntentRefunded(
        bytes32 indexed intentHash
    );

    event FusionOrderCreated(
        bytes32 indexed intentHash,
        address indexed maker,
        bytes orderData
    );

    modifier validIntent(bytes32 intentHash) {
        require(bridgeIntents[intentHash].user != address(0), "Intent does not exist");
        require(!bridgeIntents[intentHash].completed, "Intent already completed");
        require(!bridgeIntents[intentHash].refunded, "Intent already refunded");
        _;
    }

    function initialize(address unrealToken_) public initializer {
        __Ownable_init();
        __ReentrancyGuard_init();
        unrealToken = UnrealToken(unrealToken_);
    }

    /**
     * @dev Create a bridge intent with hashlock/timelock
     * @param amount Amount to bridge
     * @param targetChainId Target chain ID
     * @param hashlock Hash of the secret (keccak256 of secret)
     * @param timelock Expiration timestamp
     */
    function createBridgeIntent(
        uint256 amount,
        uint256 targetChainId,
        bytes32 hashlock,
        uint256 timelock
    ) external nonReentrant returns (bytes32 intentHash) {
        require(amount > 0, "Amount must be greater than 0");
        require(timelock > block.timestamp, "Timelock must be in the future");
        require(hashlock != bytes32(0), "Invalid hashlock");

        // Generate unique intent hash
        intentHash = keccak256(abi.encodePacked(
            msg.sender,
            amount,
            block.chainid,
            targetChainId,
            hashlock,
            timelock,
            block.timestamp
        ));

        // Store bridge intent
        bridgeIntents[intentHash] = BridgeIntent({
            user: msg.sender,
            amount: amount,
            sourceChainId: block.chainid,
            targetChainId: targetChainId,
            hashlock: hashlock,
            timelock: timelock,
            completed: false,
            refunded: false
        });

        // Lock $UNREAL tokens in bridge by transferring to bridge contract
        require(unrealToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        emit BridgeIntentCreated(
            intentHash,
            msg.sender,
            amount,
            block.chainid,
            targetChainId,
            hashlock,
            timelock
        );

        // Emit Fusion+ order creation event
        bytes memory orderData = abi.encode(intentHash, amount, targetChainId);
        emit FusionOrderCreated(intentHash, msg.sender, orderData);

        return intentHash;
    }

    /**
     * @dev Complete bridge intent by revealing secret
     * @param intentHash Hash of the bridge intent
     * @param secret Secret that matches the hashlock
     */
    function completeBridgeIntent(
        bytes32 intentHash,
        bytes32 secret
    ) external validIntent(intentHash) nonReentrant {
        BridgeIntent storage intent = bridgeIntents[intentHash];

        // Verify secret matches hashlock
        require(keccak256(abi.encodePacked(secret)) == intent.hashlock, "Invalid secret");
        require(block.timestamp <= intent.timelock, "Intent expired");

        // Mark as completed
        intent.completed = true;

        // Release locked $UNREAL tokens to user
        require(unrealToken.transfer(intent.user, intent.amount), "Transfer failed");

        emit BridgeIntentCompleted(intentHash, secret);
    }

    /**
     * @dev Refund bridge intent after timelock expires
     * @param intentHash Hash of the bridge intent
     */
    function refundBridgeIntent(
        bytes32 intentHash
    ) external validIntent(intentHash) nonReentrant {
        BridgeIntent storage intent = bridgeIntents[intentHash];

        require(block.timestamp > intent.timelock, "Intent not expired");
        require(msg.sender == intent.user, "Only user can refund");

        // Mark as refunded
        intent.refunded = true;

        // Refund $UNREAL tokens back to user
        require(unrealToken.transfer(intent.user, intent.amount), "Transfer failed");

        emit BridgeIntentRefunded(intentHash);
    }

    /**
     * @dev Get bridge intent details
     * @param intentHash Hash of the bridge intent
     */
    function getBridgeIntent(bytes32 intentHash) external view returns (BridgeIntent memory) {
        return bridgeIntents[intentHash];
    }

    /**
     * @dev Emergency function to update UNREAL token address
     * @param newUnrealToken New UNREAL token address
     */
    function setUnrealToken(address newUnrealToken) external onlyOwner {
        unrealToken = UnrealToken(newUnrealToken);
    }
}

contracts/UnrealHTLC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "./UnrealToken.sol";

/**
 * @title UnrealHTLC (Hash Time Lock Contract)
 * @dev This contract implements the HTLC functionality for cross-chain swaps between
 * Etherlink and NEAR using the 1inch Fusion+ pattern.
 */
contract UnrealHTLC is Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable {
    // Lock contract structure for HTLC
    struct LockContract {
        bytes32 secretHash;     // Hash of the secret (preimage)
        address payable recipient; // Recipient address
        address payable sender;    // Sender address
        uint256 amount;         // Amount of tokens locked
        uint256 endtime;        // Expiration timestamp
        bool withdrawn;         // Whether the funds have been withdrawn
        bool refunded;          // Whether the funds have been refunded
        string preimage;        // The revealed secret (empty until revealed)
    }

    // Token contract
    UnrealToken public unrealToken;

    // Mapping from lock contract ID to lock contract details
    mapping(bytes32 => LockContract) public lockContracts;

    // Events
    event SwapInitiated(
        bytes32 indexed lockContractId,
        bytes32 secretHash,
        address indexed recipient,
        address indexed sender,
        uint256 amount,
        uint256 endtime,
        string targetChain,
        string targetAddress
    );

    event SwapWithdrawn(
        bytes32 indexed lockContractId,
        string preimage
    );

    event SwapRefunded(
        bytes32 indexed lockContractId
    );

    event CrossChainSwapCompleted(
        bytes32 lockContractId,
        string sourceChain,
        string sourceAddress,
        address destinationAddress,
        uint256 amount,
        string preimage
    );

    // Modifiers
    modifier futureEndtime(uint256 endtime) {
        require(endtime > block.timestamp, "End time must be in the future");
        _;
    }

    modifier lockContractExists(bytes32 lockContractId) {
        require(hasLockContract(lockContractId), "Lock contract does not exist");
        _;
    }

    modifier matchesSecretHash(bytes32 lockContractId, string memory preimage) {
        require(
            lockContracts[lockContractId].secretHash == sha256(abi.encodePacked(preimage)),
            "Secret hash does not match"
        );
        _;
    }

    modifier withdrawable(bytes32 lockContractId) {
        require(lockContracts[lockContractId].recipient == msg.sender, "Not the recipient");
        require(!lockContracts[lockContractId].withdrawn, "Already withdrawn");
        require(!lockContracts[lockContractId].refunded, "Already refunded");
        _;
    }

    modifier refundable(bytes32 lockContractId) {
        require(lockContracts[lockContractId].sender == msg.sender, "Not the sender");
        require(!lockContracts[lockContractId].withdrawn, "Already withdrawn");
        require(!lockContracts[lockContractId].refunded, "Already refunded");
        require(lockContracts[lockContractId].endtime <= block.timestamp, "Timelock not expired");
        _;
    }

    /**
     * @dev Initialize the contract
     * @param _unrealToken Address of the UnrealToken contract
     */
    function initialize(UnrealToken _unrealToken) public initializer {
        __Ownable_init();
        __ReentrancyGuard_init();

        unrealToken = _unrealToken;
    }

    /**
     * @dev Initiates a cross-chain swap by locking tokens in the contract
     * @param secretHash Hash of the secret (sha256)
     * @param recipient Address that can withdraw the tokens with the secret
     * @param amount Amount of tokens to lock
     * @param endtime Timestamp after which the sender can reclaim the tokens
     * @param targetChain Target blockchain name (e.g. "NEAR")
     * @param targetAddress Target address on the other chain
     * @return lockContractId ID of the created lock contract
     */
    function initiateSwap(
        bytes32 secretHash,
        address payable recipient,
        uint256 amount,
        uint256 endtime,
        string calldata targetChain,
        string calldata targetAddress
    ) external nonReentrant futureEndtime(endtime) returns (bytes32 lockContractId) {
        require(amount > 0, "Amount must be greater than 0");

        // Generate a unique lock contract ID
        lockContractId = sha256(
            abi.encodePacked(
                secretHash,
                recipient,
                msg.sender,
                amount,
                endtime,
                block.timestamp
            )
        );

        // Make sure it doesn't already exist
        require(!hasLockContract(lockContractId), "Lock contract already exists");

        // Transfer tokens from the sender to this contract
        require(
            unrealToken.transferFrom(msg.sender, address(this), amount),
            "Token transfer failed"
        );

        // Store the lock contract details
        lockContracts[lockContractId] = LockContract({
            secretHash: secretHash,
            recipient: recipient,
            sender: payable(msg.sender),
            amount: amount,
            endtime: endtime,
            withdrawn: false,
            refunded: false,
            preimage: ""
        });

        // Emit event
        emit SwapInitiated(
            lockContractId,
            secretHash,
            recipient,
            msg.sender,
            amount,
            endtime,
            targetChain,
            targetAddress
        );

        return lockContractId;
    }

    /**
     * @dev Withdraw tokens by revealing the secret
     * @param lockContractId ID of the lock contract
     * @param preimage Secret that hashes to the secretHash
     * @return success True if successful
     */
    function withdraw(
        bytes32 lockContractId,
        string memory preimage
    )
        external
        nonReentrant
        lockContractExists(lockContractId)
        matchesSecretHash(lockContractId, preimage)
        withdrawable(lockContractId)
        returns (bool success)
    {
        // Get the lock contract
        LockContract storage lockContract = lockContracts[lockContractId];

        // Update the contract
        lockContract.preimage = preimage;
        lockContract.withdrawn = true;

        // Transfer tokens to the recipient
        require(
            unrealToken.transfer(lockContract.recipient, lockContract.amount),
            "Token transfer failed"
        );

        // Emit event
        emit SwapWithdrawn(lockContractId, preimage);

        return true;
    }

    /**
     * @dev Refund tokens to the sender if the timelock has expired
     * @param lockContractId ID of the lock contract
     * @return success True if successful
     */
    function refund(
        bytes32 lockContractId
    )
        external
        nonReentrant
        lockContractExists(lockContractId)
        refundable(lockContractId)
        returns (bool success)
    {
        // Get the lock contract
        LockContract storage lockContract = lockContracts[lockContractId];

        // Update the contract
        lockContract.refunded = true;

        // Transfer tokens back to the sender
        require(
            unrealToken.transfer(lockContract.sender, lockContract.amount),
            "Token transfer failed"
        );

        // Emit event
        emit SwapRefunded(lockContractId);

        return true;
    }

    /**
     * @dev Complete a cross-chain swap from another chain (to be called by relayer/oracle)
     * @param sourceChain Source blockchain name (e.g. "NEAR")
     * @param sourceAddress Source address on the other chain
     * @param destinationAddress Address to receive tokens on Etherlink
     * @param amount Amount of tokens to transfer
     * @param preimage Secret that was revealed on the source chain
     */
    function completeSwap(
        string calldata sourceChain,
        string calldata sourceAddress,
        address destinationAddress,
        uint256 amount,
        string calldata preimage
    ) external onlyOwner nonReentrant {
        // Generate a unique ID for this cross-chain completion
        bytes32 lockContractId = sha256(
            abi.encodePacked(
                sourceChain,
                sourceAddress,
                destinationAddress,
                amount,
                preimage
            )
        );

        // Mint or transfer tokens to the destination address
        // (Assuming we have minting rights, otherwise would need to be pre-funded)
        unrealToken.mint(destinationAddress, amount);

        // Emit event
        emit CrossChainSwapCompleted(
            lockContractId,
            sourceChain,
            sourceAddress,
            destinationAddress,
            amount,
            preimage
        );
    }

    /**
     * @dev Check if a lock contract exists
     * @param lockContractId ID of the lock contract
     * @return exists True if the contract exists
     */
    function hasLockContract(bytes32 lockContractId) public view returns (bool) {
        return lockContracts[lockContractId].sender != address(0);
    }

    /**
     * @dev Get details of a lock contract
     * @param lockContractId The ID of the lock contract to get
     * @return secretHash The hash of the secret
     * @return recipient The recipient address
     * @return sender The sender address
     * @return amount The token amount
     * @return endtime The expiry timestamp
     * @return withdrawn Whether the tokens were withdrawn
     * @return refunded Whether the tokens were refunded
     * @return preimage The revealed preimage (if withdrawn)
     */
    function getLockContract(bytes32 lockContractId)
        external
        view
        returns (
            bytes32 secretHash,
            address recipient,
            address sender,
            uint256 amount,
            uint256 endtime,
            bool withdrawn,
            bool refunded,
            string memory preimage
        )
    {
        require(hasLockContract(lockContractId), "Lock contract does not exist");

        LockContract storage lockContract = lockContracts[lockContractId];

        return (
            lockContract.secretHash,
            lockContract.recipient,
            lockContract.sender,
            lockContract.amount,
            lockContract.endtime,
            lockContract.withdrawn,
            lockContract.refunded,
            lockContract.preimage
        );
    }
}

contracts/UnrealT1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PermitUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20CappedUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20BurnableUpgradeable.sol";

// TODO:
// For proxy routing usecase

// 1. Burn balance should be Erc20 token not mapping
// 2. Users should be able to transferFrom burnBalance , increaseAllowance at $UNREAL token cost . But rest like normal transfer disabled.

// 3. $UNREAL burned has no utility, its used to proxy requests
// 4. Proxy :

// New major changes

// transfer: Only whitelisted addresses can transfer: mapping(address => bool) public whitelisted; including owner
// Max pay at a time is MAX_COST = 1ether
// Min pay at a time is MIN_COST = 1wei=
// TODO: name should be hardcoded and lets use a good emoji if its okay

contract UnrealT1 is
    Initializable,
    ERC20PermitUpgradeable,
    ERC20PausableUpgradeable,
    ERC20CappedUpgradeable,
    ERC20BurnableUpgradeable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable
{
    uint256 public constant COST = 1 ether; // 1 $UNREAL token with 18 decimals
    // address mapped to (epoch => amount)
    mapping(address => mapping(uint256 => uint256)) public burnBalance;
    uint256 public burnEpoch = 0;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
        // can only deploy via proxy
    }

    function initialize(uint256 initialSupply) public initializer {
        string memory name_ = "Unreal T1";
        string memory symbol_ = "UNREAL";
        uint256 MAX_SUPPLY = 2_500_000_000 ether; //2.5B $UNREAL tokens

        __ERC20_init(name_, symbol_);
        __ERC20Permit_init(name_);
        __ERC20Capped_init(MAX_SUPPLY);
        __Pausable_init();
        __Ownable_init();
        __ReentrancyGuard_init();

        _mint(msg.sender, initialSupply);
    }

    // --- Pausable ---
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // --- Minting ---
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    // --- Burning ---
    function burn(address from, uint256 amount) external onlyOwner {
        super._burn(from, amount); //Punishment not burn token
    }

    function _burn(address from, uint256 amount) internal override {
        super._burn(from, amount);
        burnBalance[from][burnEpoch] += amount;
    }

    // FIXME: no need of burn epochs and all
    function incrementBurnEpoch() external onlyOwner {
        burnEpoch++;
    }

    // FIXME: penialize with Burned Tokens if exist.
    // --- Owner-only: Slash Burn Options ---
    event BurnOptionsSlashed(
        address indexed account,
        uint256 epoch,
        uint256 amount
    );

    function slashBurnOptions(
        address account,
        uint256 amount
    ) external onlyOwner {
        burnBalance[account][burnEpoch] -= amount; //underflow: protected
        emit BurnOptionsSlashed(account, burnEpoch, amount);
    }

    // --- Transfer From with Cost Enforcement and Burning ---
    // nonReentrant is kept for transferFrom as it interacts with external accounts and has custom logic
    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public override nonReentrant returns (bool) {
        require(from != to, "Sender and recipient cannot be the same");
        require(msg.sender == to, "Only the recipient can call transferFrom"); //Anyone with allowance can call
        // require(amount == COST, "Amount must be exactly equal to the cost");
        super.transferFrom(from, to, amount);
        _burn(to, amount);
        return true;
    }

    // --- Transfer with Whole Token Enforcement ---
    function transfer(
        address to,
        uint256 amount
    ) public override returns (bool) {
        require(
            amount % 1 ether == 0,
            "Transfer amount must be whole tokens (no decimals)"
        );
        return super.transfer(to, amount);
    }

    function _mint(
        address to,
        uint256 amount
    ) internal override(ERC20Upgradeable, ERC20CappedUpgradeable) {
        // calls ERC20CappedUpgradeable in linearization order
        super._mint(to, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20Upgradeable, ERC20PausableUpgradeable) {
        super._beforeTokenTransfer(from, to, amount);
    }
}

